let keyVal = msg.key;
let slideVal = msg.payload;
if (keyVal == "left") {
    msg.slide = slideVal - .5;
}  else if (keyVal == "right") {
    slideVal = slideVal + .5;
} else {
    slideVal = slideVal;
}
msg.payload = slideVal;
return msg;

const MIN = 16;
const MAX = 24;
const INC = 0.5;

let slider = msg.slider || MIN;

switch(msg.payload) {
    case 37:
        //left arrow
        slider -= INC;
    break;

    case 39:
        //right arrow
        slider += INC;
    break;
}

if (slider < MIN) {
    slider = MIN;
} else if (slider > MAX) {
    slider = MAX;
}

msg.payload = slider;

return msg;

